import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom"
import { useData } from '../hooks/DataContext';

function SearchResult() {
    const { data, loading } = useData();
    const { searchTerm } = useParams()
    const [filteredResuls, setFilteredResults] = useState([]);
console.log('data', data, searchTerm)

    useEffect(() => {
        const filteredData =  data.states.filter((item) =>
            item.state.toLowerCase().includes(searchTerm.toLowerCase())
        );
        console.log('filteredData', filteredData, searchTerm)
        setFilteredResults(filteredData);
    }, [])

    const renderSearchjResults = () => {
        return filteredResuls && filteredResuls.map((result) => {
            return  <article><img className="card-image" src={result.imgurl} />
                <div className='content'>
                    <h3>{result.place}</h3>
                    <p>{result.desc}</p>
                    </div>
                    </article>
                })
    }


    return (<>{!loading ? <div>Loading...</div> : <div className='wrapper'>
        <div className='wrapper-header'>Search Results</div>

        <div className='card-grid'>{renderSearchjResults()}</div>
    </div>}

    </>)
}

export default SearchResult;
