import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

function BurgerMenu() {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpenBar, setIsOpenBar] = useState(true);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };
const handleOnchange = () => {
    setIsOpenBar(!isOpenBar)
}
  return (
    <nav role="navigation">

  <div id="menuToggle">

    <input type="checkbox" id="menuCheckbox" onChange={(e) => handleOnchange(e)} />
    {isOpenBar ? <i className="margin-wrapper fa fa-bars"></i> : <i className="margin-wrapper fas fa-times"></i>}


    {/* <span></span>
    <span></span>
    <span></span> */}

   <ul className='burger'><NavLink to="/Home">Home</NavLink></ul>
    <ul id="menu">

      <li>
        <a href="#">
          <label htmlFor="menuCheckbox" onClick={handleToggle}>Home</label>
        </a>
      </li>
      <li>
        <a href="/about">
          <label htmlFor="menuCheckbox" onClick={handleToggle}>About</label>
        </a>
      </li>


      <li><label htmlFor="menuCheckbox"><a>Info</a></label></li>
      <li><label htmlFor="menuCheckbox"><a>Contact</a></label></li>


      <li>
        <a href="https://erikterwan.com/" target="_blank">Show me more</a>
      </li>
    </ul>
  </div>
</nav>
  );
}

export default BurgerMenu;
