import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useData } from "../hooks/DataContext";

function SearchBar() {
    const { data, loading } = useData();
    const [searchTerm, setSearchTerm] = useState("");
    const [suggestions, setSuggestions] = useState([]);
    const [showsuggestions, setShowSugegestions] = useState(false);
    const navigate = useNavigate();

    const handleSearch = (e) => {
        const term = e.target.value;
        setSearchTerm(term);
        if (term.length > 0) {
            const filteredData = data.states.filter((item) =>
                item.state.toLowerCase().includes(term.toLowerCase())
            );
            console.log('filteredData', filteredData, data)
            setSuggestions(filteredData);
            setShowSugegestions(true);
        } else {
            setShowSugegestions(false);
        }
    };

    const handleSugessionClick = (suggestion) => {
        setSearchTerm(suggestion.state)
        setShowSugegestions(false);
        const val = suggestion.state;
        navigate(`/search/${val}`)
    }
    console.log('dataaa', data)

    return (
        <>{!loading ? <div>loading...</div> : <div className="search"><div>
            <div className="search-bar">
                <i className="icon fa fa-search"></i>
                <input className="input-text" type="text" name="search" value={searchTerm} onChange={(e) => handleSearch(e)} />
                <i className='slider-icon fas fa-sliders-h'></i>
            </div>
            </div>

            {showsuggestions && (
                <div className="search-list">
                    {suggestions.map((suggestion, index) => (
                        <div key={index} onClick={() => handleSugessionClick(suggestion)}>{suggestion.state}</div>
                    ))}
                </div>
            )}
        </div>}</>


    );
}

export default SearchBar;
