import React, { useState } from "react";
import { NavLink } from "react-router-dom";

function BurgerMenu({ handleBurgerClick }) {
  const [isOpenBar, setIsOpenBar] = useState(true);

  const handleOnchange = () => {
    console.log("drop");
    setIsOpenBar(!isOpenBar);
    handleBurgerClick();
  };

  return (
    <nav role="navigation">
      <div id="menuToggle">
        <input type="checkbox" id="menuCheckbox" onChange={handleOnchange} />
        {isOpenBar ? (
          <i className="margin-wrapper fa fa-bars"></i>
        ) : (
          <i className="margin-wrapper fas fa-times"></i>
        )}

        <div id="menu">
          <NavLink className="nav-link" to="menu/home">
            Home
          </NavLink>
          <NavLink className="nav-link" to="/home">
            About
          </NavLink>
          <NavLink className="nav-link" to="/Home">
            Contact
          </NavLink>
          <NavLink className="nav-link" to="/Home">
            Info
          </NavLink>
          <NavLink className="nav-link" to="/Home">
            Portfolio
          </NavLink>
          <NavLink className="nav-link" to="/Home">
            Services
          </NavLink>
        </div>
      </div>
    </nav>
  );
}

export default BurgerMenu;
