import React, { useState } from 'react';

function BurgerMenu() {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };
const handleOnchange = () => {
    var checkBox = document.getElementById("menuCheckbox");
    if(checkBox.checked == true){
        return <i class="fa fa-bars"></i>
    } else {
        return <i className='grid-icon fas fa-th-large'></i>
    }
}
  return (
    <nav role="navigation">

  <div id="menuToggle">

    <input type="checkbox" id="menuCheckbox" onChange={(e) => handleOnchange(e)} />

    <div className='bar-icon'></div>

    {/* <span></span>
    <span></span>
    <span></span> */}


    <ul id="menu">

      <li>
        <a href="#">
          <label htmlFor="menuCheckbox" onClick={handleToggle}>Home</label>
        </a>
      </li>
      <li>
        <a href="#about">
          <label htmlFor="menuCheckbox" onClick={handleToggle}>About</label>
        </a>
      </li>


      <li><label htmlFor="menuCheckbox"><a>Info</a></label></li>
      <li><label htmlFor="menuCheckbox"><a>Contact</a></label></li>


      <li>
        <a href="https://erikterwan.com/" target="_blank">Show me more</a>
      </li>
    </ul>
  </div>
</nav>
  );
}

export default BurgerMenu;
