/* eslint-disable no-unused-vars */
import React, { useCallback, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useData } from "../../hooks/DataContext";
import "./searchBox.css";
import { LOADING } from "../../constants";

function SearchBar() {
  const { data, loading } = useData();
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [showsuggestions, setShowSugegestions] = useState(false);
  const navigate = useNavigate();

  const handleSearch = useCallback(() => {
    console.log('callback', searchTerm)
    if (searchTerm.length > 0) {
      const filterByState = [...new Set(data.states.map((item) => item.state))];
      console.log("filterByState", filterByState);
      const filteredData = filterByState.filter((item) =>
        item.toLowerCase().includes(searchTerm.toLowerCase())
      );
      console.log("filteredData", filteredData, data);
      if (filteredData.length > 0) {
        setSearchResults(filteredData)
        setSuggestions(filteredData);
        setShowSugegestions(true);
      }
    } else {
      setShowSugegestions(false);
    }
  },[searchTerm, data]);

  useEffect(()=>{
    handleSearch();
  }, [searchTerm, handleSearch])

  // useEffect(()=>{
  //   if(searchResults.length > 0){
  //     navigate(`/search/${searchResults}`);
  //   }

  // }, [searchResults])

  const handleSugessionClick = (suggestion) => {
    setSearchTerm(suggestion.state);
    setShowSugegestions(false);
    navigate(`/search/${suggestion}`);
  };

const handleInputChange = (e) =>{
  const searchValueOnChange = e.target.value
  console.log('change', searchValueOnChange)
  setSearchTerm(searchValueOnChange)
}
  const handleKeyDown = (e) => {

    var enterValue = e.key 
    console.log('keydown',enterValue)
    if(enterValue === 'Enter') {
      const searchValueOnClick = e.target.value
      console.log('keydown',searchValueOnClick)
    }

    // navigate(`/search/${searchValueOnClick}`);
  };
  console.log("searchTerm", searchTerm);

  return (
    <>
      {loading ? (
        <div>{LOADING}</div>
      ) : (
        <div className="search">
          <div className="input-box relative-position">
            <i className="icon curser-pointer fa fa-search"></i>
            <input
              type="text"
              name="search"
              autocomplete="off"
              value={searchTerm}
              onChange={(e) => handleInputChange(e)}
              handleKeyDown={(e) => handleKeyDown(e)}
            />
            <div className="slider-icon curser-pointer">
              <i className="fas fa-sliders-h"></i>
            </div>
          </div>

          {showsuggestions && (
            <div className="search-list">
              {suggestions.map((suggestion, index) => (
                <div
                  className="search-item"
                  key={index}
                  onClick={() => handleSugessionClick(suggestion)}
                >
                  {suggestion}
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </>
  );
}

export default SearchBar;
