import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom"
import data from '../data.json'
import axios from 'axios';
import { useData } from '../hooks/DataContext';

function SearchResult() {
    const { data, loading } = useData();

        let { searchTerm } = useParams()
    console.log('param', searchTerm)
    const [searchResults, setSearchResults] = useState(data);
    const [filteredResuls, setFilteredResults] = useState([]);
    const term = "karnataka"
    // useEffect(() => {
    //     axios.get("http://localhost:3000/db/data.json")
    //         .then((res) => { setSearchResults(res.data) })
    //         .catch((err) => console.log(err))
    // }, [])


    useEffect(() => {
        const filteredData = data.states.filter((item) =>
            item.state.toLowerCase().includes(searchTerm.toLowerCase())
        );
        console.log('filteredData', filteredData)
        setFilteredResults(filteredData);
    }, [searchResults])

    const renderSearchjResults = () => {
        return filteredResuls.map((result) => {
            return <div><img src={result.imgurl} /><div>{result.desc}</div></div>
        })
    }

    console.log('searchResults', searchResults)
    return (<>
        <div className='wrapper'>
            <div className='wrapper-header'>Search Results</div>
            {renderSearchjResults()}
        </div>
    </>)
}

export default SearchResult;
