import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from "react-router-dom"
import { useData } from '../hooks/DataContext';

function SearchResult({isOpen}) {
    const { data, loading } = useData();
    const { searchTerm } = useParams()
    const [searchResults, setResults] = useState(data)
    const [filteredResuls, setFilteredResults] = useState([]);
console.log('data', data, searchTerm, loading)
const angle = `${isOpen ? 'margin-left-wrapper' : 'margin-center-wrapper'}`;
    useEffect(() => {
        console.log('sdksd', Object.keys(searchResults))
        if (Object.keys(searchResults).length !== 0){
            const filteredData =   searchResults.states.filter((item) =>
                item.state.toLowerCase().includes(searchTerm.toLowerCase())
            );
            console.log('filteredData', filteredData, searchTerm)
            setFilteredResults(filteredData);
        } 
        else {
            console.log('else')
        }

    }, [searchResults])


    const renderSearchjResults = useCallback(() => {
        let filteredData;
        if (Object.keys(searchResults).length !== 0){
             filteredData =   searchResults.states.filter((item) =>
                item.state.toLowerCase().includes(searchTerm.toLowerCase())
            );
            console.log('filteredData', filteredData, searchTerm)
            setFilteredResults(filteredData);
        } 
        return filteredData && filteredData.map((result) => {
            return  <article><img className="card-image" src={result.imgurl} />
                <div className='content'>
                    <h3>{result.place}</h3>
                    <p>{result.desc}</p>
                    </div>
                    </article>
                })
    }, [data])

    return (<>{loading ? <div>Loading...</div> : <div className={angle}>
        <div className='wrapper-header'>Search Results</div>

        <div className='card-grid'>{renderSearchjResults()}</div>
    </div>}

    </>)
}

export default SearchResult;
