import React, { useState } from "react";

function SearchBar() {
  const [searchTerm, setSearchTerm] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [showsuggestions, setShowSugegestions] = useState(false);
  const data = ["apple", "banana", "mango"];

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    if (term.length > 0) {
      const filteredData = data.filter((item) =>
        item.toLowerCase().includes(term.toLowerCase())
      );
      console.log('filteredData', filteredData, data)
      setSuggestions(filteredData);
      setShowSugegestions(true);
    } else {
        setShowSugegestions(false);
    }
  };

  const handleSugessionClick = (suggestion) => {
    setSearchTerm(suggestion)
    setShowSugegestions(false);
  }

  return (
    <div className="search">
      <input
        type="text"
        name="search"
        value={searchTerm}
        onChange={(e) => handleSearch(e)}
      />
      {showsuggestions && (
        <ul>
          {suggestions.map((suggestion, index) => (
            <li key={index} onClick={()=> handleSugessionClick(suggestion)}>{suggestion}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SearchBar;
