import React, { useState } from 'react';

function BurgerMenu() {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="burger-menu">
      <button className="burger-menu-toggle" onClick={handleToggle}>
        <span className="burger-menu-toggle-icon"></span>
        <span className="burger-menu-toggle-icon"></span>
        <span className="burger-menu-toggle-icon"></span>
      </button>
      {isOpen && (
        <nav className="burger-menu-nav">
          <ul>
            <li>
              <a href="#">Home</a>
            </li>
            <li>
              <a href="#">About</a>
            </li>
            <li>
              <a href="#">Contact</a>
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
}

export default BurgerMenu;
